\documentclass[tikz,border=10pt]{standalone}
\usepackage{tikz}

\begin{document}

\begin{tikzpicture}[line width=1.5pt]
    % Wheels
    \draw (0,0) circle (2cm);
    \draw (6,0) circle (2cm);

